import React, { useEffect, useState } from 'react';
import SensorController from '../../controllers/SensorController';
import DeviceController from '../../controllers/DeviceController';
import './Dashboard.css';

const Dashboard = () => {
  const [sensorData, setSensorData] = useState({
    temperature: 0,
    humidity: 0,
    motion: false
  });
  
  const [deviceStats, setDeviceStats] = useState({
    total: 0,
    online: 0,
    offline: 0
  });
  
  const [alerts, setAlerts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  
  useEffect(() => {
    const loadDashboardData = async () => {
      setIsLoading(true);
      try {
        // Load sensor data
        const sensorResult = await SensorController.getLatestReadings();
        setSensorData(sensorResult);
        
        // Load device statistics
        const deviceResult = await DeviceController.getDeviceStats();
        setDeviceStats(deviceResult);
        
        // Load recent alerts
        const alertsResult = await SensorController.getRecentAlerts();
        setAlerts(alertsResult);
      } catch (error) {
        console.error('Error loading dashboard data:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    loadDashboardData();
    
    // Set up polling interval
    const interval = setInterval(loadDashboardData, 30000);
    
    // Clean up interval on unmount
    return () => clearInterval(interval);
  }, []);
  
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };
  
  const DashboardItem = ({ icon, label, value, unit }) => {
    console.log(`Rendering ${label} with value:`, value, typeof value);
    
    // Định dạng giá trị số để hiển thị
    const formattedValue = typeof value === 'number' 
      ? value.toFixed(1) 
      : value;
    
    return (
      <div className="dashboard-item">
        <div className={`dashboard-icon ${label.toLowerCase()}`}>
          {icon}
        </div>
        <div className="dashboard-info">
          <div className="dashboard-value">{formattedValue}{unit}</div>
          <div className="dashboard-label">{label}</div>
        </div>
      </div>
    );
  };
  
  if (isLoading) {
    return <div className="loading-indicator">Loading dashboard data...</div>;
  }
  
  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      
      <div className="dashboard-grid">
        {/* Device Statistics */}
        <div className="card">
          <h2>Devices</h2>
          <div className="stats">
            <div className="stat">
              <span className="stat-value">{deviceStats.total}</span>
              <span className="stat-label">Total</span>
            </div>
            <div className="stat">
              <span className="stat-value stat-online">{deviceStats.online}</span>
              <span className="stat-label">Online</span>
            </div>
            <div className="stat">
              <span className="stat-value stat-offline">{deviceStats.offline}</span>
              <span className="stat-label">Offline</span>
            </div>
          </div>
        </div>
        
        {/* Current Sensor Readings */}
        <div className="card">
          <h2>Current Readings</h2>
          <div className="readings">
            <DashboardItem icon={<i className="fas fa-thermometer-half"></i>} label="Temperature" value={sensorData.temperature} unit="°C" />
            <DashboardItem icon={<i className="fas fa-tint"></i>} label="Humidity" value={sensorData.humidity} unit="%" />
            <DashboardItem icon={<i className={`fas fa-running ${sensorData.motion ? 'active' : ''}`}></i>} label="Motion" value={sensorData.motion ? 'Detected' : 'None'} />
          </div>
        </div>
        
        {/* Recent Alerts */}
        <div className="card">
          <h2>Recent Alerts</h2>
          {alerts.length > 0 ? (
            <ul className="alert-list">
              {alerts.map((alert) => (
                <li key={alert.id} className={`alert ${alert.status}`}>
                  <div className="alert-icon">
                    {alert.type === 'temperature' && <i className="fas fa-thermometer-full"></i>}
                    {alert.type === 'humidity' && <i className="fas fa-tint"></i>}
                    {alert.type === 'motion' && <i className="fas fa-running"></i>}
                  </div>
                  <div className="alert-details">
                    <span className="alert-message">{alert.message}</span>
                    <span className="alert-time">{formatTime(alert.timestamp)}</span>
                  </div>
                  <div className="alert-status">
                    <span className={alert.status}>{alert.status}</span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p className="no-alerts">No recent alerts</p>
          )}
          <div className="card-footer">
            <a href="/alerts" className="view-all">View All Alerts</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
