import axios from 'axios';

// Get the API base URL from environment variables
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
console.log('API Base URL:', API_BASE_URL); // For debugging purposes

// Create an axios instance with default config
const apiService = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Request interceptor to add authorization header
apiService.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle common errors
apiService.interceptors.response.use(
  (response) => {
    console.log('API Response:', response); // For debugging purposes
    return response;
  },
  (error) => {
    console.error('API Error:', error.response || error); // For debugging purposes
    
    // Handle unauthorized errors (401)
    if (error.response && error.response.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
      return Promise.reject({
        success: false,
        message: 'Phiên đăng nhập đã hết hạn'
      });
    }
    
    // Handle server errors (500)
    if (error.response && error.response.status === 500) {
      console.error('Server error:', error.response.data);
      return Promise.reject({
        success: false,
        message: 'Lỗi máy chủ, vui lòng thử lại sau'
      });
    }
    
    // Handle network errors
    if (!error.response) {
      console.error('Network error - không thể kết nối đến máy chủ');
      return Promise.reject({
        success: false,
        message: 'Không thể kết nối đến máy chủ. Vui lòng kiểm tra kết nối mạng hoặc máy chủ đã được khởi động.'
      });
    }
    
    return Promise.reject(error)
  }
);

export default apiService;
