import React, { useState, useEffect } from 'react';
import DeviceController from '../../controllers/DeviceController';
import './DeviceControl.css';

const DeviceControl = () => {
  const [devices, setDevices] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all');
  
  useEffect(() => {
    loadDevices();
  }, []);
  
  const loadDevices = async () => {
    setIsLoading(true);
    setError(null);
    
    try {
      const result = await DeviceController.getAllDevices();
      setDevices(result);
    } catch (err) {
      setError('Failed to load devices. Please try again.');
      console.error('Error loading devices:', err);
    } finally {
      setIsLoading(false);
    }
  };
  
  const handleToggleDevice = async (deviceId) => {
    try {
      const deviceIndex = devices.findIndex(d => d.id === deviceId);
      if (deviceIndex === -1) return;
      
      const device = devices[deviceIndex];
      const newStatus = device.status === 'active' ? 'inactive' : 'active';
      
      // Update UI immediately for better user experience
      const updatedDevices = [...devices];
      updatedDevices[deviceIndex] = { 
        ...device, 
        status: newStatus,
        lastUpdated: new Date().toISOString()
      };
      setDevices(updatedDevices);
      
      // Call API to update device status
      await DeviceController.updateDeviceStatus(deviceId, newStatus);
    } catch (err) {
      setError('Failed to control device. Please try again.');
      console.error('Error controlling device:', err);
      
      // Refresh devices to reflect actual state
      loadDevices();
    }
  };
  
  // Filter devices based on selected type
  const filteredDevices = filter === 'all' 
    ? devices 
    : devices.filter(device => device.type === filter);
  
  const deviceTypeIcons = {
    light: 'fas fa-lightbulb',
    fan: 'fas fa-fan',
    lock: 'fas fa-lock',
    alarm: 'fas fa-bell'
  };
  
  if (isLoading) {
    return <div className="loading-indicator">Loading devices...</div>;
  }
  
  return (
    <div className="device-control">
      <div className="page-header">
        <h1>Device Control</h1>
        <div className="filter-buttons">
          <button 
            className={filter === 'all' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('all')}
          >
            All
          </button>
          <button 
            className={filter === 'light' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('light')}
          >
            Lights
          </button>
          <button 
            className={filter === 'fan' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('fan')}
          >
            Fans
          </button>
          <button 
            className={filter === 'lock' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('lock')}
          >
            Locks
          </button>
        </div>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      <div className="device-grid">
        {filteredDevices.length > 0 ? (
          filteredDevices.map(device => (
            <div key={device.id} className="device-card">
              <div className="device-header">
                <div className="device-icon">
                  <i className={deviceTypeIcons[device.type] || 'fas fa-plug'}></i>
                </div>
                <div className="device-info">
                  <h3>{device.name}</h3>
                  <p>{device.location}</p>
                </div>
                <div className="device-status">
                  <span className={device.status}>{device.status === 'active' ? 'ON' : 'OFF'}</span>
                </div>
              </div>
              
              <div className="device-control-panel">
                <label className="toggle-switch">
                  <input
                    type="checkbox"
                    checked={device.status === 'active'}
                    onChange={() => handleToggleDevice(device.id)}
                  />
                  <span className="toggle-slider"></span>
                </label>
              </div>
            </div>
          ))
        ) : (
          <div className="no-devices">No devices found matching your filter criteria.</div>
        )}
      </div>
    </div>
  );
};

export default DeviceControl;
