import React, { useState, useEffect } from 'react';
import AlertConfigController from '../../controllers/AlertConfigController';
import './AlertConfig.css';

const AlertConfig = () => {
  const [configs, setConfigs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editingConfig, setEditingConfig] = useState(null);
  
  // Load alert configurations on mount
  useEffect(() => {
    loadConfigs();
  }, []);
  
  const loadConfigs = async () => {
    setIsLoading(true);
    setError(null);
    
    try {
      const configData = await AlertConfigController.getAlertConfigs();
      setConfigs(configData);
    } catch (err) {
      console.error('Error loading configurations:', err);
      setError(err.message || 'Failed to load alert configurations. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };
  
  const handleToggleActive = async (configId, currentActive) => {
    try {
      await AlertConfigController.updateAlertConfig(configId, { isActive: !currentActive });
      
      // Update the local state
      setConfigs(configs.map(config => 
        config.id === configId ? { ...config, isActive: !currentActive } : config
      ));
      
      setError(null);
    } catch (err) {
      console.error('Error updating configuration:', err);
      setError(err.message || 'Failed to update alert configuration. Please try again.');
    }
  };
  
  const handleReset = async () => {
    try {
      const resetConfigs = await AlertConfigController.resetAlertConfigs();
      setConfigs(resetConfigs);
      setError(null);
    } catch (err) {
      console.error('Error resetting configurations:', err);
      setError(err.message || 'Failed to reset alert configurations. Please try again.');
    }
  };
  
  const startEditing = (config) => {
    setEditingConfig({
      ...config,
      minValue: config.minValue.toString(),
      maxValue: config.maxValue.toString()
    });
    setIsEditing(true);
  };
  
  const cancelEditing = () => {
    setEditingConfig(null);
    setIsEditing(false);
  };
  
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEditingConfig({
      ...editingConfig,
      [name]: type === 'checkbox' ? checked : value
    });
  };
  
  const saveConfig = async (e) => {
    e.preventDefault();
    
    try {
      const updatedConfig = await AlertConfigController.updateAlertConfig(
        editingConfig.id, 
        {
          minValue: parseFloat(editingConfig.minValue),
          maxValue: parseFloat(editingConfig.maxValue),
          isActive: editingConfig.isActive
        }
      );
      
      // Update the local state
      setConfigs(configs.map(config => 
        config.id === updatedConfig.id ? updatedConfig : config
      ));
      
      setIsEditing(false);
      setEditingConfig(null);
      setError(null);
    } catch (err) {
      console.error('Error saving configuration:', err);
      setError(err.message || 'Failed to save alert configuration. Please try again.');
    }
  };
  
  const getSensorTypeLabel = (type) => {
    switch (type) {
      case 'temperature':
        return 'Temperature';
      case 'humidity':
        return 'Humidity';
      default:
        return type.charAt(0).toUpperCase() + type.slice(1);
    }
  };
  
  const getSensorTypeUnit = (type) => {
    switch (type) {
      case 'temperature':
        return 'Â°C';
      case 'humidity':
        return '%';
      default:
        return '';
    }
  };
  
  if (isLoading) {
    return <div className="loading-indicator">Loading configurations...</div>;
  }
  
  return (
    <div className="alert-config-container">
      <div className="alert-config-header">
        <h1>Alert Configuration</h1>
        <button 
          className="reset-btn" 
          onClick={handleReset}
        >
          Reset to Default
        </button>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {configs.length > 0 ? (
        <div className="config-grid">
          {configs.map(config => (
            <div key={config.id} className="config-card">
              {isEditing && editingConfig && editingConfig.id === config.id ? (
                <form onSubmit={saveConfig} className="config-form">
                  <div className="config-form-header">
                    <h3>{getSensorTypeLabel(config.sensorType)} Alert Settings</h3>
                  </div>
                  
                  <div className="form-group">
                    <label>Minimum Value:</label>
                    <div className="input-with-unit">
                      <input
                        type="number"
                        name="minValue"
                        value={editingConfig.minValue}
                        onChange={handleInputChange}
                        step="0.1"
                        required
                      />
                      <span className="unit">{getSensorTypeUnit(config.sensorType)}</span>
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label>Maximum Value:</label>
                    <div className="input-with-unit">
                      <input
                        type="number"
                        name="maxValue"
                        value={editingConfig.maxValue}
                        onChange={handleInputChange}
                        step="0.1"
                        required
                      />
                      <span className="unit">{getSensorTypeUnit(config.sensorType)}</span>
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        name="isActive"
                        checked={editingConfig.isActive}
                        onChange={handleInputChange}
                      />
                      Enable alerts
                    </label>
                  </div>
                  
                  <div className="form-actions">
                    <button type="submit" className="save-btn">Save</button>
                    <button type="button" className="cancel-btn" onClick={cancelEditing}>Cancel</button>
                  </div>
                </form>
              ) : (
                <>
                  <div className="config-header">
                    <h3>{getSensorTypeLabel(config.sensorType)} Alert Settings</h3>
                    <div className="config-status">
                      <span className={config.isActive ? 'active' : 'inactive'}>
                        {config.isActive ? 'Enabled' : 'Disabled'}
                      </span>
                    </div>
                  </div>
                  
                  <div className="config-details">
                    <div className="threshold">
                      <span className="label">Min Value:</span>
                      <span className="value">{config.minValue} {getSensorTypeUnit(config.sensorType)}</span>
                    </div>
                    <div className="threshold">
                      <span className="label">Max Value:</span>
                      <span className="value">{config.maxValue} {getSensorTypeUnit(config.sensorType)}</span>
                    </div>
                  </div>
                  
                  <div className="config-actions">
                    <button 
                      className="edit-btn"
                      onClick={() => startEditing(config)}
                    >
                      Edit
                    </button>
                    <button 
                      className={`toggle-btn ${config.isActive ? 'active' : 'inactive'}`}
                      onClick={() => handleToggleActive(config.id, config.isActive)}
                    >
                      {config.isActive ? 'Disable' : 'Enable'}
                    </button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      ) : (
        <div className="no-configs">
          <p>No alert configurations found.</p>
          <button 
            className="reset-btn" 
            onClick={handleReset}
          >
            Initialize Default Configurations
          </button>
        </div>
      )}
    </div>
  );
};

export default AlertConfig;
